<?phprequire_once 'config.php';echo "Checking recent entries with unix timestamps:" . PHP_EOL;$result = $pdo->query('SELECT action, timestamp, UNIX_TIMESTAMP(timestamp) as unix_ts FROM audit_trail ORDER BY timestamp DESC LIMIT 3');foreach ($result as $row) {    echo "- " . $row['action'] . PHP_EOL;    echo "  DB time: " . $row['timestamp'] . PHP_EOL;    echo "  Unix TS: " . $row['unix_ts'] . PHP_EOL;    echo "  JS TS: " . ($row['unix_ts'] * 1000) . PHP_EOL;    echo "  Current diff: " . (time() - $row['unix_ts']) . " seconds" . PHP_EOL;    echo PHP_EOL;}echo "Current time: " . date('Y-m-d H:i:s') . PHP_EOL;echo "Current Unix: " . time() . PHP_EOL;?>